INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES
(1, 1, '2016-10-03 11:57:22', '2016-10-03 17:57:22', 'Bienvenido a WordPress. Esta es tu primera entrada. Edítala o bórrala, ¡y comienza a publicar!.', '¡Hola mundo!', '', 'publish', 'open', 'open', '', 'hola-mundo', '', '', '2016-10-03 11:57:22', '2016-10-03 17:57:22', '', 0, 'http://localhost/El%20ancon/?p=1', 0, 'post', '', 1),
(2, 1, '2016-10-03 11:57:22', '2016-10-03 17:57:22', 'Esto es una página de ejemplo. Es diferente a una entrada porque permanece fija en un lugar y se mostrará en la navegación de tu sitio (en la mayoría de los temas). La mayoría de la gente empieza con una página de Acerca de, que les presenta a los potenciales visitantes del sitio. Podría ser algo como esto:\n\n<blockquote>¡Hola! Soy mensajero por el día, aspirante a actor por la noche, y este es mi blog. Vivo en Madrid, tengo un perrazo llamado Duque y me gustan las piñas coladas (y que me pille un chaparrón)</blockquote>\n\n...o algo así:\n\n<blockquote>La empresa XYZ se fundó en 1971 y ha estado ofreciendo "cosas" de calidad al público desde entonces. Situada en Madrid, XYZ emplea a más de 2.000 personas y hace todo tipo de cosas sorprendentes para la comunidad de Madrid.</blockquote>\n\nSi eres nuevo en WordPress deberías ir a <a href="http://localhost/El%20ancon/wp-admin/">tu escritorio</a> para borrar esta página y crear páginas nuevas con tu propio contenido. ¡Pásalo bien!', 'Página de ejemplo', '', 'publish', 'closed', 'open', '', 'pagina-ejemplo', '', '', '2016-10-03 11:57:22', '2016-10-03 17:57:22', '', 0, 'http://localhost/El%20ancon/?page_id=2', 0, 'page', '', 0),
(3, 1, '2016-10-03 11:57:53', '0000-00-00 00:00:00', '', 'Borrador automático', '', 'auto-draft', 'open', 'open', '', '', '', '', '2016-10-03 11:57:53', '0000-00-00 00:00:00', '', 0, 'http://localhost/El%20ancon/?p=3', 0, 'post', '', 0),
(4, 1, '2016-10-06 11:21:02', '2016-10-06 17:21:02', '', 'Tienda', '', 'publish', 'closed', 'closed', '', 'tienda', '', '', '2016-10-06 11:21:02', '2016-10-06 17:21:02', '', 0, 'http://localhost/El%20ancon/index.php/tienda/', 0, 'page', '', 0),
(5, 1, '2016-10-06 11:21:02', '2016-10-06 17:21:02', '[woocommerce_cart]', 'Carrito', '', 'publish', 'closed', 'closed', '', 'carro', '', '', '2016-10-06 11:21:02', '2016-10-06 17:21:02', '', 0, 'http://localhost/El%20ancon/index.php/carro/', 0, 'page', '', 0),
(6, 1, '2016-10-06 11:21:03', '2016-10-06 17:21:03', '[woocommerce_checkout]', 'Pago', '', 'publish', 'closed', 'closed', '', 'finalizar-comprar', '', '', '2016-10-06 11:21:03', '2016-10-06 17:21:03', '', 0, 'http://localhost/El%20ancon/index.php/finalizar-comprar/', 0, 'page', '', 0),
(7, 1, '2016-10-06 11:21:03', '2016-10-06 17:21:03', '[woocommerce_my_account]', 'Mi cuenta', '', 'publish', 'closed', 'closed', '', 'mi-cuenta', '', '', '2016-10-06 11:21:03', '2016-10-06 17:21:03', '', 0, 'http://localhost/El%20ancon/index.php/mi-cuenta/', 0, 'page', '', 0),
(8, 1, '2016-10-06 11:23:24', '0000-00-00 00:00:00', '', 'Borrador automático', '', 'auto-draft', 'open', 'closed', '', '', '', '', '2016-10-06 11:23:24', '0000-00-00 00:00:00', '', 0, 'http://localhost/El%20ancon/?post_type=product&p=8', 0, 'product', '', 0);

